<?php\n// URL of the web page to retrieve\n$url = "http://example.com";\n\n// Retrieve the web page content\n$content = file_get_contents($url);\n\n// Check if the content was retrieved successfully\nif ($content === FALSE) {\n    die("Error: Unable to retrieve the web page.");\n}\n\n// The text to search for\n$searchText = "certain text";\n\n// Search for the text in the content\nif (strpos($content, $searchText) !== FALSE) {\n    echo "Text found!";\n} else {\n    echo "Text not found.";\n}\n?>