$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Assuming $userrole and $member_id are arrays\n$userroles = $_POST['userrole']; // Example: ['admin', 'user']\n$member_ids = $_POST['member_id']; // Example: [1, 2]\n\n$query = "UPDATE members SET userrole = ? WHERE member_id = ?";\n$stmt = $conn->prepare($query);\n\nif ($stmt === false) {\n    die("Prepare failed: " . $conn->error);\n}\n\nforeach ($userroles as $index => $role) {\n    $id = $member_ids[$index];\n    $stmt->bind_param("si", $role, $id);\n    $stmt->execute();\n}\n\n$stmt->close();\n$conn->close();