if ($word_of_the_day) {\n    $stmt = $wpdb->prepare("SELECT term, definition FROM glossary WHERE term = %s", $word_of_the_day);\n    $wotd = $wpdb->get_results($stmt);\n    foreach ($wotd as $term) { }\n} elseif ($_COOKIE['WOTD']) {\n    $word_of_the_day = htmlspecialchars($_COOKIE['WOTD'], ENT_QUOTES, 'UTF-8');\n    $stmt = $wpdb->prepare("SELECT term, definition FROM glossary WHERE term = %s", $word_of_the_day);\n    $wotd = $wpdb->get_results($stmt);\n    foreach ($wotd as $term) { }\n} else {\n    $wotd = $wpdb->get_results("SELECT term, definition FROM glossary ORDER BY RAND() LIMIT 1");\n    foreach ($wotd as $term) {\n        setcookie("WOTD", $term->term, time() + 86400, "", "", true, true);\n    }\n}