<?php\n$log_file = 'loop_log.txt';\n$counter = 0;\n$max_iterations = 1000;\n\n// Example loop with a condition\nwhile (true) {\n    error_log("Iteration: $counter\n", 3, $log_file);\n\n    // Your loop logic here\n    // For example:\n    if ($counter >= $max_iterations) {\n        error_log("Max iterations reached, terminating loop\n", 3, $log_file);\n        break;\n    }\n\n    // Simulate some condition that might eventually become false\n    $some_condition = ($counter < 500); // Example condition\n    if (!$some_condition) {\n        break;\n    }\n\n    $counter++;\n}\n?>