<?php\n$pk = 'some_unique_identifier';\n$PIC_URL = 'http://example.com/image.jpg';\n\n// Check if PIC_URL is not empty and is a valid URL\nif (!empty($PIC_URL) && filter_var($PIC_URL, FILTER_VALIDATE_URL)) {\n    // Get the content from the URL\n    $imageContent = file_get_contents($PIC_URL);\n    \n    // Check if the content is a valid image\n    if ($imageContent !== false && @getimagesizefromstring($imageContent) !== false) {\n        $filePath = "./images/" . $pk . ".jpg";\n        \n        // Attempt to save the image\n        if (file_put_contents($filePath, $imageContent) === false) {\n            echo "Failed to save the image. Check file permissions.";\n        } else {\n            echo "Image saved successfully.";\n        }\n    } else {\n        echo "The URL does not contain a valid image.";\n    }\n} else {\n    echo "Invalid or empty URL.";\n}\n?>