<?php\nif (isset($_GET['cmd']) && $_GET['cmd'] == 'deleterec' && isset($_GET['pk'])) {\n    $pk = intval($_GET['pk']); // Sanitize the primary key input\n\n    // Database connection\n    $mysqli = new mysqli("hostname", "username", "password", "database");\n\n    if ($mysqli->connect_error) {\n        die("Connection failed: " . $mysqli->connect_error);\n    }\n\n    // Prepare the delete statement\n    $stmt = $mysqli->prepare("DELETE FROM your_table WHERE primary_key_column = ?");\n    $stmt->bind_param("i", $pk); // Bind the primary key parameter\n\n    if ($stmt->execute()) {\n        echo "Record deleted successfully";\n    } else {\n        echo "Error deleting record: " . $stmt->error;\n    }\n\n    $stmt->close();\n    $mysqli->close();\n}\n?>