<?php\n   use PHPMailer\PHPMailer\PHPMailer;\n   use PHPMailer\PHPMailer\Exception;\n\n   require 'vendor/autoload.php';\n\n   function emailUser($table, $subject, $message) {\n       $query = "SELECT * FROM $table";\n       $result = mysql_query($query);\n\n       while ($row = mysql_fetch_array($result)) {\n           $to = $row['email'];\n           $to_all .= '<li>' . $row['email'] . '</li>';\n\n           $mail = new PHPMailer(true);\n\n           try {\n               // Server settings\n               $mail->isSMTP();\n               $mail->Host = 'mail.site.net';\n               $mail->SMTPAuth = false; // Enable SMTP authentication if needed\n               $mail->Port = 25;\n\n               // Sender and recipient settings\n               $mail->setFrom('email@site.net', 'Sender Name');\n               $mail->addAddress($to);\n\n               // Content settings\n               $mail->isHTML(true);\n               $mail->Subject = $subject;\n               $mail->Body = $message;\n\n               // Send email\n               $mail->send();\n               echo 'Message has been sent to ' . $to . '<br>';\n           } catch (Exception $e) {\n               echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}<br>";\n           }\n       }\n\n       print '<h3>Mail successfully sent to:</h3>';\n       print '<ul>' . $to_all . '</ul>';\n   }\n   ?>