require_once 'Zend/Loader.php';\nZend_Loader::loadClass('Zend_Gdata_YouTube');\n\n$yt = new Zend_Gdata_YouTube();\n$query = $yt->newVideoQuery();\n\n$query->setOrderBy($this->data['ItemVideo']['sort']);\n$query->setRacy('include');\n$query->setCategory($this->data['ItemVideo']['q']);\n\n// Set the feed type and URL\n$query->setFeedType('videos'); // Example feed type, change as necessary\n$query->setVideoQuery('http://gdata.youtube.com/feeds/api/videos');\n\ntry {\n    $videoFeed = $yt->getVideoFeed($query);\n} catch (Exception $e) {\n    echo var_dump($query) . '' . $e;\n}