<?php\n   function ftpUpload($srcDir, $destDir, $ftpConn, $exclude = ['.svn']) {\n       $files = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($srcDir),\n           RecursiveIteratorIterator::LEAVES_ONLY);\n\n       foreach ($files as $file) {\n           if (in_array($file->getFilename(), $exclude)) {\n               continue;\n           }\n\n           if (!$file->isDir()) {\n               $remoteFile = $destDir . '/' . $files->getSubPathName();\n               ftp_put($ftpConn, $remoteFile, $file->getRealPath(), FTP_BINARY);\n           }\n       }\n   }\n\n   // FTP connection setup\n   $ftpServer = "yourftpserver.com";\n   $ftpUsername = "yourusername";\n   $ftpPassword = "yourpassword";\n   $ftpConn = ftp_connect($ftpServer) or die("Could not connect to $ftpServer");\n   $login = ftp_login($ftpConn, $ftpUsername, $ftpPassword);\n   \n   if ($login) {\n       ftpUpload('/local/path/to/your/project', '/remote/path/to/your/project', $ftpConn);\n   }\n\n   ftp_close($ftpConn);\n   ?>