// application/controllers/SomeController.php\n   class SomeController extends CI_Controller {\n       public function index() {\n           $user = $this->User_model->get_user_data(); // Assume you have a User_model to fetch user data\n           $data['user_profile_box'] = render_user_profile($user);\n           $this->load->view('some_view', $data);\n       }\n\n       public function ajax_user_profile() {\n           $user = $this->User_model->get_user_data();\n           echo render_user_profile($user);\n       }\n   }