<?php\n// Enable error reporting\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\n// Connection details\n$servername = "vps2.foo.com";\n$username = "remote_vhost30";\n$password = "password";\n$dbname = "best_of";\n$port = 3306;\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname, $port);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Perform the query\n$sections_result = $conn->query("SELECT * FROM sections");\n\nif ($sections_result === false) {\n    die("Error: " . $conn->error);\n}\n\n// Fetch the result\n$sections_array = $sections_result->fetch_array(MYSQLI_ASSOC);\n\nif ($sections_array === null) {\n    die("No data found");\n}\n\n// Process the fetched data\nvar_dump($sections_array);\n\n$conn->close();\n?>