<?php\n$d = date("D");\nif (in_array($d, array('Thu', 'Fri', 'Sat', 'Sun'))) {\n    echo "The latest post for Thursday, Friday, Saturday, Sunday:<br>";\n\n    $my_query = new WP_Query(array('category_name' => 'posts', 'posts_per_page' => 1));\n    if ($my_query->have_posts()) {\n        while ($my_query->have_posts()) {\n            $my_query->the_post();\n            ?>\n            <a href="<?php the_permalink(); ?>" rel="bookmark"><?php the_title(); ?></a><br>\n            <?php\n        }\n    } else {\n        echo "No posts found.";\n    }\n    wp_reset_postdata();\n}\n?>