<?php\n// Database connection\n$servername = "your_server_name";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch existing data\n$query = "SELECT * FROM routes ORDER BY id DESC LIMIT 8";\n$result = $conn->query($query);\n\n// Example values for the comparison entry\n$econ_value = 88; // example value\n$x_value = 5; // example value\n$y_value = 6; // example value\n$galaxy_value = 2; // example value\n$planet_value = 3; // example value\n$players = 23; // example value\n\nfunction calculate_credits($econ, $distance, $players) {\n    return sqrt($econ) * (1 + sqrt($distance) / 75 + sqrt($players) / 10);\n}\n\nfunction calculate_distance($galaxy1, $x1, $y1, $planet1, $galaxy2, $x2, $y2, $planet2) {\n    if ($galaxy1 !== $galaxy2) {\n        return abs($galaxy1 - $galaxy2);\n    } elseif ($x1 !== $x2 || $y1 !== $y2) {\n        return sqrt(pow(($x1 - $x2), 2) + pow(($y1 - $y2), 2));\n    } else {\n        return abs($planet1 - $planet2);\n    }\n}\n\n// Display data with the new calculated column\nif ($result->num_rows > 0) {\n    echo "<table><tr><th>ID</th><th>Name</th><th>Base</th><th>Econ</th><th>Location</th><th>X</th><th>Y</th><th>Galaxy</th><th>Planet</th><th>Comment</th><th>IPAddress</th><th>Calculated Credits</th></tr>";\n    while($row = $result->fetch_assoc()) {\n        $distance = calculate_distance($galaxy_value, $x_value, $y_value, $planet_value, $row['galaxy'], $row['x'], $row['y'], $row['planet']);\n        $calculated_credits = calculate_credits($row['econ'], $distance, $players);\n        echo "<tr><td>{$row['id']}</td><td>{$row['name']}</td><td>{$row['base']}</td><td>{$row['econ']}</td><td>{$row['location']}</td><td>{$row['x']}</td><td>{$row['y']}</td><td>{$row['galaxy']}</td><td>{$row['planet']}</td><td>{$row['comment']}</td><td>{$row['ipaddress']}</td><td>{$calculated_credits}</td></tr>";\n    }\n    echo "</table>";\n} else {\n    echo "0 results";\n}\n\n$conn->close();\n?>