// Validate and sanitize input\n$username = filter_input(INPUT_POST, 'username', FILTER_SANITIZE_STRING);\n$email = filter_input(INPUT_POST, 'email', FILTER_VALIDATE_EMAIL);\n$address = filter_input(INPUT_POST, 'address', FILTER_SANITIZE_STRING);\n$password = $_POST['password']; // Passwords should be hashed, not sanitized\n\nif (!$username || !$email || !$address || empty($password)) {\n    // Handle validation error\n    die('Invalid input');\n}\n\n// Escape output when displaying\necho htmlspecialchars($username, ENT_QUOTES, 'UTF-8');\necho htmlspecialchars($email, ENT_QUOTES, 'UTF-8');\necho htmlspecialchars($address, ENT_QUOTES, 'UTF-8');