<?php\n// Assuming you have a database connection already established\n// User ID for the current user\n$user_id = 1; // Change this to the current user's ID\n\n// Query to get the user's join date\n$query = "SELECT join_date FROM users WHERE id = ?";\n$stmt = $pdo->prepare($query);\n$stmt->execute([$user_id]);\n$row = $stmt->fetch(PDO::FETCH_ASSOC);\n\nif ($row) {\n    $join_date = new DateTime($row['join_date']);\n    $current_date = new DateTime();\n\n    // Calculate the interval between join date and current date\n    $interval = $join_date->diff($current_date);\n\n    // Generate the list of months\n    $months = [];\n    for ($i = 0; $i <= $interval->y * 12 + $interval->m; $i++) {\n        $month = clone $join_date;\n        $month->modify("+$i months");\n        $months[] = $month->format('F Y'); // e.g., "July 2009"\n    }\n\n    // Display the list of months\n    echo "<ul>";\n    foreach ($months as $month) {\n        echo "<li>" . htmlspecialchars($month) . "</li>";\n    }\n    echo "</ul>";\n} else {\n    echo "User not found.";\n}\n?>