<?php\n// Define the file path\n$file_path = realpath(__DIR__ . '/../data/file.txt');\n\n// Check if the file exists\nif (file_exists($file_path)) {\n    // Read the file content\n    $content = file_get_contents($file_path);\n    echo "File Content: " . htmlspecialchars($content) . "<br>";\n\n    // Append new data to the file\n    $new_data = "\nNew line of text";\n    if (file_put_contents($file_path, $content . $new_data) !== false) {\n        echo "Data successfully written to the file.";\n    } else {\n        echo "Failed to write data to the file.";\n    }\n} else {\n    echo "File does not exist.";\n}\n?>