function debugPDOQuery($query, $params) {\n    $keys = array();\n    $values = array();\n    \n    // build a regular expression for each parameter\n    foreach ($params as $key => $value) {\n        // Check if named parameters (':param') or anonymous parameters ('?')\n        if (is_string($key)) {\n            $keys[] = '/:' . ltrim($key, ':') . '/';\n        } else {\n            $keys[] = '/[?]/';\n        }\n        \n        // Escape the parameter value for safe insertion into query string\n        $values[] = is_numeric($value) ? $value : '"' . addslashes($value) . '"';\n    }\n    \n    // Replace the parameters with the values in the query string\n    $query = preg_replace($keys, $values, $query, 1, $count);\n    \n    // Return the debug query\n    return $query;\n}\n\n// Usage example\n$sql = "SELECT * FROM users WHERE username = :username AND password = :password";\n$params = array(':username' => 'john_doe', ':password' => 'secret');\n\n$debugQuery = debugPDOQuery($sql, $params);\necho $debugQuery;