<?php\n$ip = $_SERVER['REMOTE_ADDR'];\n$date = date("j. F Y");\n$time = date("H:i:s");\n\n// Read the existing content of the HTML file\n$htmlContent = file_get_contents('./iplogg.html');\n\n// Prepare the new row to be inserted\n$newRow = "<tr><td>{$ip}</td><td>{$date}</td><td>{$time}</td></tr>\n";\n\n// Find the position of the </tbody> tag\n$position = strrpos($htmlContent, '</tbody>');\n\nif ($position !== false) {\n    // Insert the new row before the </tbody> tag\n    $updatedContent = substr($htmlContent, 0, $position) . $newRow . substr($htmlContent, $position);\n\n    // Write the updated content back to the HTML file\n    file_put_contents('./iplogg.html', $updatedContent);\n} else {\n    // Handle the case where the </tbody> tag is not found\n    echo "Error: </tbody> tag not found in the HTML file.";\n}\n?>