class My_Form extends Zend_Form\n{\n    public function init()\n    {\n        // Your form elements initialization here\n\n        // Apply the custom decorator to all hidden elements\n        foreach ($this->getElements() as $element) {\n            if ($element instanceof Zend_Form_Element_Hidden) {\n                $element->addDecorator(new My_Form_Decorator_HiddenElement());\n            }\n        }\n    }\n}