class My_Form_Decorator_HiddenElement extends Zend_Form_Decorator_Abstract\n{\n    public function render($content)\n    {\n        $element = $this->getElement();\n        if ($element instanceof Zend_Form_Element_Hidden) {\n            $dtClass = 'hidden';\n            $ddClass = 'hidden';\n\n            $dtContent = '<dt class="' . $dtClass . '">&nbsp;</dt>';\n            $ddContent = '<dd class="' . $ddClass . '">' . $content . '</dd>';\n\n            return $dtContent . $ddContent;\n        }\n\n        return $content;\n    }\n}\n\n// Apply the custom decorator to all hidden elements\nclass My_Form extends Zend_Form\n{\n    public function init()\n    {\n        foreach ($this->getElements() as $element) {\n            if ($element instanceof Zend_Form_Element_Hidden) {\n                $element->addDecorator(new My_Form_Decorator_HiddenElement());\n            }\n        }\n    }\n}