<?php\n// Sample XML data (normally this would be posted to your server)\n$xmlData = <<<XML\n<?xml version='1.0' standalone='yes'?>\n<XML>\n  <FoxyCart Version='0.6'>\n    <transaction_id>616</transaction_id>\n    <transaction_date>2007-05-04 20:53:57</transaction_date>\n    <customer_id>122</customer_id>\n    <first_name>Dirk</first_name>\n    <last_name>Gently</last_name>\n    <total_price>4.38</total_price>\n    <amount>24.38</amount>\n    <product>\n      <code>foo</code>\n      <price>20.00</price>\n      <quantity>1</quantity>\n      <weight>0.10</weight>\n    </product>\n    <shipping>\n      <first_name>John</first_name>\n      <last_name>Doe</last_name>\n      <address_1>2345 Some Address</address_1>\n      <city>Some City</city>\n      <state>TN</state>\n      <postal_code>37013</postal_code>\n      <country>US</country>\n      <shipping_service>DHL: Next Afternoon</shipping_service>\n      <shipping_total>52.15</shipping_total>\n    </shipping>\n  </FoxyCart>\n</XML>\nXML;\n\n// Load the XML data into a SimpleXMLElement object\n$xml = simplexml_load_string($xmlData);\n\n// Convert the SimpleXMLElement object to a JSON string, then decode to an array\n$json = json_encode($xml);\n$array = json_decode($json, true);\n\n// Print the resulting array\nprint_r($array);\n\n// Now, you can insert the data into a MySQL database\n// Example: Inserting transaction details into a MySQL table\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$transaction_id = $mysqli->real_escape_string($array['FoxyCart']['transaction_id']);\n$transaction_date = $mysqli->real_escape_string($array['FoxyCart']['transaction_date']);\n$customer_id = $mysqli->real_escape_string($array['FoxyCart']['customer_id']);\n$first_name = $mysqli->real_escape_string($array['FoxyCart']['first_name']);\n$last_name = $mysqli->real_escape_string($array['FoxyCart']['last_name']);\n$total_price = $mysqli->real_escape_string($array['FoxyCart']['total_price']);\n$amount = $mysqli->real_escape_string($array['FoxyCart']['amount']);\n\n$sql = "INSERT INTO transactions (transaction_id, transaction_date, customer_id, first_name, last_name, total_price, amount) \n        VALUES ('$transaction_id', '$transaction_date', '$customer_id', '$first_name', '$last_name', '$total_price', '$amount')";\n\nif ($mysqli->query($sql) === TRUE) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $sql . "<br>" . $mysqli->error;\n}\n\n$mysqli->close();\n?>