function array_unique2($array) \n{ \n    $to_return = array(); \n    $current_index = 0;\n    $array_count = count($array);\n\n    for ($i = 0; $i < $array_count; $i++) \n    { \n        $current_is_unique = true; \n\n        for ($a = $i + 1; $a < $array_count; $a++) \n        { \n            if ($array[$i] == $array[$a]) \n            { \n                $current_is_unique = false; \n                break; \n            } \n        } \n        if ($current_is_unique) \n        { \n            $to_return[$current_index++] = $array[$i];\n        } \n    } \n    return $to_return; \n}