// Extend Zend_Db_Adapter_Pdo_Sqlite\nclass My_Db_Adapter_Pdo_Sqlite extends Zend_Db_Adapter_Pdo_Sqlite\n{\n    public function lastInsertId($name = null)\n    {\n        $stmt = $this->_connection->query('SELECT last_insert_rowid()');\n        $result = $stmt->fetch(PDO::FETCH_NUM);\n        return $result[0];\n    }\n}\n\n// Usage example\n$db = new My_Db_Adapter_Pdo_Sqlite(array(\n    'dbname' => 'path/to/your/database.sqlite'\n));\n\n// Perform an insert operation\n$db->insert('your_table', array('column1' => 'value1'));\n\n// Get the last inserted ID\n$lastId = $db->lastInsertId();\necho "Last Inserted ID: " . $lastId;