<?php\ninclude "header.php";\ninclude "wrap.php";\ninclude "left.php";\ninclude "ad728x90.php";\ninclude "utilplaces.php";\n\n// Connect to the Database using mysqli\n$mysqli = new mysqli("localhost", "user", "password", "databasename");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Grab the variable from the URL\n$bbname = isset($_GET['bbname']) ? $mysqli->real_escape_string($_GET['bbname']) : '';\n\n// Construct the query\n$query = "SELECT * FROM places";\nif (!empty($bbname)) {\n    $query .= " WHERE name LIKE '$bbname%'";\n}\n$query .= " ORDER BY name ASC LIMIT 0, 30";\n\n// Execute the query\n$result = $mysqli->query($query);\n\nif (!$result) {\n    die("Query failed: " . $mysqli->error);\n}\n\n// Display the results\necho "<table>";\necho "<tr><th>Name</th><th>Details</th><th>Hours</th><th>Type</th><th>Area</th><th>Rating</th></tr>";\n\nwhile ($info = $result->fetch_assoc()) {\n    echo "<tr>";\n    echo "<td>" . htmlspecialchars($info['name']) . "</td>";\n    echo "<td>" . htmlspecialchars($info['address']) . "<br>" . htmlspecialchars($info['phonenumber']) . "</td>";\n    echo "<td>" . htmlspecialchars($info['hours']) . "</td>";\n    echo "<td>" . htmlspecialchars($info['type']) . "</td>";\n    echo "<td>" . htmlspecialchars($info['district']) . "</td>";\n    echo "<td>Pending<!--RATING--></td>";\n    echo "</tr>";\n}\n\necho "</table>";\n\n$mysqli->close();\n\ninclude "feetie.php";\n?>