<?php\n// Database connection details for the source and destination servers\n$srcHost = 'source_host';\n$srcUser = 'source_user';\n$srcPass = 'source_password';\n$srcDb   = 'source_database';\n\n$destHost = 'destination_host';\n$destUser = 'destination_user';\n$destPass = 'destination_password';\n$destDb   = 'destination_database';\n\n// Connect to the source database\n$srcConn = new mysqli($srcHost, $srcUser, $srcPass, $srcDb);\nif ($srcConn->connect_error) {\n    die("Source connection failed: " . $srcConn->connect_error);\n}\n\n// Connect to the destination database\n$destConn = new mysqli($destHost, $destUser, $destPass, $destDb);\nif ($destConn->connect_error) {\n    die("Destination connection failed: " . $destConn->connect_error);\n}\n\n// Get the list of tables from the source database\n$tablesResult = $srcConn->query("SHOW TABLES");\nif ($tablesResult) {\n    while ($tableRow = $tablesResult->fetch_row()) {\n        $tableName = $tableRow[0];\n\n        // Get the CREATE TABLE statement for the current table\n        $createTableResult = $srcConn->query("SHOW CREATE TABLE $tableName");\n        if ($createTableResult) {\n            $createTableRow = $createTableResult->fetch_assoc();\n            $createTableSql = $createTableRow['Create Table'];\n\n            // Drop the table if it already exists in the destination database\n            $destConn->query("DROP TABLE IF EXISTS $tableName");\n\n            // Create the table in the destination database\n            if ($destConn->query($createTableSql) === TRUE) {\n                echo "Table $tableName copied successfully\n";\n            } else {\n                echo "Error copying table $tableName: " . $destConn->error . "\n";\n            }\n        }\n    }\n} else {\n    echo "Error retrieving tables: " . $srcConn->error . "\n";\n}\n\n// Close connections\n$srcConn->close();\n$destConn->close();\n?>