<?php\n$target_dir = "images/";\n$target_file = $target_dir . basename($_FILES["photo"]["name"]);\n$uploadOk = 1;\n$imageFileType = strtolower(pathinfo($target_file, PATHINFO_EXTENSION));\n\n// Check if file is a actual image or fake image\nif (isset($_POST["submit"])) {\n    $check = getimagesize($_FILES["photo"]["tmp_name"]);\n    if ($check !== false) {\n        echo "File is an image - " . $check["mime"] . ".<br>";\n        $uploadOk = 1;\n    } else {\n        echo "File is not an image.<br>";\n        $uploadOk = 0;\n    }\n}\n\n// Check file size\nif ($_FILES["photo"]["size"] > 350000) {\n    echo "Sorry, your file is too large. 35Kb is the largest file you can upload.<br>";\n    $uploadOk = 0;\n}\n\n// Allow certain file formats\nif ($imageFileType != "jpg" && $imageFileType != "png" && $imageFileType != "jpeg" && $imageFileType != "gif") {\n    echo "Sorry, only JPG, JPEG, PNG & GIF files are allowed.<br>";\n    $uploadOk = 0;\n}\n\n// Check if $uploadOk is set to 0 by an error\nif ($uploadOk == 0) {\n    echo "Sorry, your file was not uploaded.<br>";\n// if everything is ok, try to upload file\n} else {\n    if (move_uploaded_file($_FILES["photo"]["tmp_name"], $target_file)) {\n        echo "The file ". basename($_FILES["photo"]["name"]). " has been uploaded.<br>";\n        \n        // Use mysqli or PDO to connect to the database securely\n        $mysqli = new mysqli("localhost", "username", "password", "project");\n\n        // Check connection\n        if ($mysqli->connect_error) {\n            die("Connection failed: " . $mysqli->connect_error);\n        }\n\n        // Prepare and bind\n        $stmt = $mysqli->prepare("INSERT INTO dbProfile (nameMember, bandMember, photo, aboutMember, otherBands) VALUES (?, ?, ?, ?, ?)");\n        $stmt->bind_param("sssss", $name, $bandMember, $pic, $about, $bands);\n\n        // Set parameters and execute\n        $name = $_POST['nameMember'];\n        $bandMember = $_POST['bandMember'];\n        $pic = basename($_FILES["photo"]["name"]);\n        $about = $_POST['aboutMember'];\n        $bands = $_POST['otherBands'];\n\n        if ($stmt->execute()) {\n            echo "New record created successfully.<br>";\n        } else {\n            echo "Error: " . $stmt->error;\n        }\n\n        $stmt->close();\n        $mysqli->close();\n    } else {\n        echo "Sorry, there was an error uploading your file.<br>";\n    }\n}\n?>