<?php\n// Start the session at the very beginning\nsession_start();\n\n// Ensure the session cookie parameters are set correctly\nsession_set_cookie_params(0, '/', '.example.com', isset($_SERVER["HTTPS"]), true);\n\nrequire './classes/DBInterface.php';\n$db = new DBInterface();\n\nif (isset($_POST['submit']) && $_POST['submit'] == 'Login') {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    echo '-- login -- ' . htmlspecialchars($username);\n    $rs = $db->verify($username, $password, "admin", 0);\n    $admin = $rs->current();\n    if ($rs->valid()) {\n        $_SESSION['username'] = $username;\n    }\n}\n\necho ' -- session id -- ';\nvar_dump(session_id());\necho ' -- session var -- ';\nvar_dump($_SESSION);\n?>