<?php\nclass Database {\n    private $pdo;\n\n    public function __construct() {\n        $dsn = 'mysql:host=' . DB_HOST . ';dbname=' . DB_NAME;\n        $this->pdo = new PDO($dsn, DB_USER, DB_PASS);\n    }\n\n    public function query($sql, $params = []) {\n        $stmt = $this->pdo->prepare($sql);\n        $stmt->execute($params);\n        return $stmt;\n    }\n\n    // Other database methods...\n}