<?php\n\nclass ReportField {\n    private $key;\n    private $label;\n    private $accessLevel;\n    private $sqlDescriptor;\n\n    private static $fields = [];\n\n    private function __construct($key, $label, $accessLevel, $sqlDescriptor) {\n        $this->key = $key;\n        $this->label = $label;\n        $this->accessLevel = $accessLevel;\n        $this->sqlDescriptor = $sqlDescriptor;\n    }\n\n    public static function init() {\n        self::$fields['foo'] = new ReportField('foo', 'Foo', 1, 'foo as my_foo');\n        // Add other fields here\n    }\n\n    public static function getByKey($key) {\n        if (isset(self::$fields[$key])) {\n            return self::$fields[$key];\n        }\n        return null; // or throw an exception\n    }\n\n    public function getFieldSql() {\n        return $this->sqlDescriptor;\n    }\n}\n\n// Initialize the fields\nReportField::init();\n\n// Example usage\n$reportPrefs = [\n    (object)['field' => 'foo'],\n    // Add other report preferences here\n];\n\n$selectFields = [];\nforeach ($reportPrefs as $pref) {\n    $field = ReportField::getByKey($pref->field);\n    if ($field !== null) {\n        $selectFields[] = $field->getFieldSql();\n    }\n}\n\nprint_r($selectFields);\n?>