function categorizeMimeType($mimeType) {\n    $mimeCategories = [\n        'office' => [\n            'application/msword',\n            'application/vnd.oasis.opendocument.text',\n            'application/pdf',\n            'application/vnd.openxmlformats-officedocument.wordprocessingml.document',\n            'application/vnd.ms-excel',\n            'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',\n            'application/vnd.oasis.opendocument.spreadsheet',\n            'application/vnd.ms-powerpoint',\n            'application/vnd.openxmlformats-officedocument.presentationml.presentation'\n        ],\n        'image' => [\n            'image/jpeg',\n            'image/png',\n            'image/gif',\n            'image/bmp',\n            'image/webp',\n            'image/svg+xml'\n        ],\n        'application' => [\n            'application/json',\n            'application/xml',\n            'application/xhtml+xml',\n            'application/javascript',\n            'application/octet-stream'\n        ],\n        'compressed' => [\n            'application/zip',\n            'application/x-7z-compressed',\n            'application/x-rar-compressed',\n            'application/x-tar',\n            'application/gzip'\n        ],\n        // Add more categories and MIME types as needed\n    ];\n\n    foreach ($mimeCategories as $category => $mimeTypes) {\n        if (in_array($mimeType, $mimeTypes)) {\n            return $category;\n        }\n    }\n\n    return 'unknown'; // Default category if MIME type is not found\n}\n\n// Example usage\n$mimeType = 'application/pdf';\n$category = categorizeMimeType($mimeType);\necho "The MIME type '$mimeType' belongs to the '$category' category.";