<?php\n// Database connection\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Execute the query\n$query = "(SELECT 'listings' AS table_name, id, date, content FROM listings)\n          UNION\n          (SELECT 'photos' AS table_name, id, date, content FROM photos)\n          UNION\n          (SELECT 'comments' AS table_name, id, date, content FROM comments)\n          ORDER BY date DESC";\n\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    // Loop through the results and format based on table name\n    while($row = $result->fetch_assoc()) {\n        switch($row['table_name']) {\n            case 'listings':\n                echo "<div class='listing-item'>";\n                echo "<h2>Listing</h2>";\n                echo "<p>ID: " . $row['id'] . "</p>";\n                echo "<p>Date: " . $row['date'] . "</p>";\n                echo "<p>" . $row['content'] . "</p>";\n                echo "</div>";\n                break;\n            case 'photos':\n                echo "<div class='photo-item'>";\n                echo "<h2>Photo</h2>";\n                echo "<p>ID: " . $row['id'] . "</p>";\n                echo "<p>Date: " . $row['date'] . "</p>";\n                echo "<p>" . $row['content'] . "</p>";\n                echo "</div>";\n                break;\n            case 'comments':\n                echo "<div class='comment-item'>";\n                echo "<h2>Comment</h2>";\n                echo "<p>ID: " . $row['id'] . "</p>";\n                echo "<p>Date: " . $row['date'] . "</p>";\n                echo "<p>" . $row['content'] . "</p>";\n                echo "</div>";\n                break;\n        }\n    }\n} else {\n    echo "No results found.";\n}\n\n$mysqli->close();\n?>