$s = new SimpleXMLElement('<root/>');\n$t = new SimpleXMLElement('<child/>');\n\n// Convert to DOM elements\n$dom_s = dom_import_simplexml($s);\n$dom_t = dom_import_simplexml($t);\n\n// Import $t into $s's document and append it\n$dom_t = $dom_s->ownerDocument->importNode($dom_t, true);\n$dom_s->appendChild($dom_t);\n\n// Convert back to SimpleXMLElement\n$s = simplexml_import_dom($dom_s);\n\necho $s->asXML();