// Generate a random salt\n$salt = bin2hex(random_bytes(16)); // 16 bytes = 128 bits\n\n// Combine salt with the password and hash it\n$password = 'bar';\n$hash = hash('sha256', $salt . $password);\n\n// Store the hash and the salt\necho "Stored Hash: $hash\n";\necho "Stored Salt: $salt\n";\n\n// To verify the password later\n$inputPassword = 'bar';\n$inputHash = hash('sha256', $salt . $inputPassword);\n\nif ($inputHash === $hash) {\n    echo "Password is valid!";\n} else {\n    echo "Invalid password.";\n}