// Class definition\nclass User {\n    public $fname;\n    public $lname;\n}\n\n// Array of objects of the class defined above\n$objUser1 = new User();\n$objUser2 = new User();\n$objUser3 = new User();\n$objUser4 = new User();\n\n// Assigning values to the properties\n$objUser1->fname = "John";\n$objUser1->lname = "Doe";\n\n$objUser2->fname = "Jane";\n$objUser2->lname = "Smith";\n\n$objUser3->fname = "Alice";\nobjUser3->lname = "Johnson";\n\n$objUser4->fname = "Bob";\n$objUser4->lname = "Brown";\n\n$alUser = array();\n$alUser[] = $objUser1;\n$alUser[] = $objUser2;\n$alUser[] = $objUser3;\n$alUser[] = $objUser4;\n\n// Iterating and extracting values\nforeach ($alUser as $user) {\n    echo "First Name: " . $user->fname . ", Last Name: " . $user->lname . "\n";\n}