<?php\n// Path to the TrueType font file\n$fontPath = '/path/to/your/fontfile.ttf';\n\n// The text string you want to calculate the bounding box for\n$text = 'Sample Text';\n\n// Font size\n$fontSize = 20;\n\n// Calculate the bounding box\n$bbox = imageftbbox($fontSize, 0, $fontPath, $text);\n\nif ($bbox === false) {\n    // Handle the error\n    die('Error: Problem loading glyph from the font file.');\n}\n\n// Proceed with your image generation logic\n?>