<?php\n    require 'vendor/autoload.php';\n\n    use PhpOffice\PhpSpreadsheet\Spreadsheet;\n    use PhpOffice\PhpSpreadsheet\Writer\Xlsx;\n    use PhpOffice\PhpSpreadsheet\Worksheet\Drawing;\n\n    // Create a new Spreadsheet object\n    $spreadsheet = new Spreadsheet();\n\n    // Set active sheet and add some data\n    $sheet = $spreadsheet->getActiveSheet();\n    $sheet->setCellValue('A1', 'Hello World!');\n\n    // Add an image to the spreadsheet\n    $drawing = new Drawing();\n    $drawing->setName('Sample Image');\n    $drawing->setDescription('Sample Image');\n    $drawing->setPath('path/to/your/image.jpg'); // Path to your image file\n    $drawing->setHeight(150); // Set the height of the image\n    $drawing->setCoordinates('B2'); // Position the image in the spreadsheet\n    $drawing->setWorksheet($spreadsheet->getActiveSheet());\n\n    // Save the spreadsheet to a file\n    $writer = new Xlsx($spreadsheet);\n    $writer->save('path/to/save/your/spreadsheet.xlsx');\n\n    echo "Spreadsheet with image created successfully!";\n    ?>