<?php\n   session_start();\n\n   // Assuming this is the page where you handle the error\n   if (isset($_GET['error_id'])) {\n       $_SESSION['error_id'] = $_GET['error_id'];\n       // Remove error_id from the URL\n       $query = $_GET;\n       unset($query['error_id']);\n       $new_query_string = http_build_query($query);\n\n       // Redirect to the same page without error_id\n       header("Location: " . strtok($_SERVER["REQUEST_URI"], '?') . '?' . $new_query_string);\n       exit;\n   }\n   ?>