<?php\n$current_year = date('Y');\n$current_month = date('n'); // Numeric representation of the current month without leading zeros\n$start_year = 2008;\n\n// Function to get month names up to a specified month\nfunction getMonthsUpTo($month) {\n    $months = [];\n    for ($m = 1; $m <= $month; $m++) {\n        $months[] = date('F', mktime(0, 0, 0, $m, 10));\n    }\n    return $months;\n}\n\n// Current year with months up to the current month\n$years[$current_year] = getMonthsUpTo($current_month);\n\n// Previous years with all months\nfor ($year = $current_year - 1; $year >= $start_year; $year--) {\n    $years[$year] = getMonthsUpTo(12);\n}\n\nprint_r($years);\n?>