<?php\n// Define the custom tick handler\nfunction tick_handler() {\n    global $start_time;\n    if (isset($start_time)) {\n        $end_time = microtime(true);\n        $parse_time = $end_time - $start_time;\n        echo "Parse time: " . $parse_time . " seconds\n";\n        // Remove the tick handler once the calculation is done to avoid overhead\n        unregister_tick_function('tick_handler');\n    }\n}\n\n// Register the tick handler\nregister_tick_function('tick_handler');\n\n// Start timing\n$start_time = microtime(true);\n\n// Declare ticks to be called every N statements (optional, default is every statement)\ndeclare(ticks=1);\n\n// Your PHP code here\n// For demonstration purposes, simulate some code execution\necho "Hello, World!\n";\n\n// End of your PHP code\n?>