<?php\ntry {\n    // Connect to the SQLite database\n    $dbh = new PDO('sqlite:vets.db');\n    \n    // Set error mode to exceptions\n    $dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    \n    // Prepare the SQL statement with placeholders\n    $stmt = $dbh->prepare("INSERT INTO vets (name, email, clinic, streetname, citystatezip, link, phone, fax, animal, region, visible) \n                           VALUES (:name, :email, :clinic, :streetname, :citystatezip, :link, :phone, :fax, :animal, :region, :visible)");\n    \n    // Bind the parameters to the statement\n    $stmt->bindParam(':name', $name);\n    $stmt->bindParam(':email', $email);\n    $stmt->bindParam(':clinic', $clinic);\n    $stmt->bindParam(':streetname', $streetname);\n    $stmt->bindParam(':citystatezip', $citystatezip);\n    $stmt->bindParam(':link', $link);\n    $stmt->bindParam(':phone', $phone);\n    $stmt->bindParam(':fax', $fax);\n    $stmt->bindParam(':animal', $animal);\n    $stmt->bindParam(':region', $region);\n    $stmt->bindParam(':visible', $visible);\n\n    // Set the parameter values\n    $name = "John Doe";\n    $email = "john.doe@example.com";\n    $clinic = "Happy Pets Clinic";\n    $streetname = "123 Pet Street";\n    $citystatezip = "Pet City, PC 12345";\n    $link = "http://happypets.example.com";\n    $phone = "123-456-7890";\n    $fax = "098-765-4321";\n    $animal = "Dog";\n    $region = "North";\n    $visible = 1;\n    \n    // Execute the statement\n    $stmt->execute();\n    \n    echo "Record inserted successfully";\n    \n    // Close the database connection\n    $dbh = null;\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>