<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Retrieve XML content from database\n$sql = "SELECT xml_content FROM xml_data WHERE id = ? LIMIT 1";\n$stmt = $conn->prepare($sql);\n$id = 1; // Replace with the appropriate ID\n$stmt->bind_param("i", $id);\n$stmt->execute();\n$stmt->bind_result($xmlContent);\n$stmt->fetch();\n$stmt->close();\n\n$conn->close();\n\n// Save the XML content to a file\nfile_put_contents('playlist.xml', $xmlContent);\n?>