<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Read XML file contents\n$xmlContent = file_get_contents('playlist.xml');\n\n// Insert XML content into database\n$stmt = $conn->prepare("INSERT INTO xml_data (xml_content) VALUES (?)");\n$stmt->bind_param("s", $xmlContent);\n$stmt->execute();\n$stmt->close();\n\n$conn->close();\n?>