<?php\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Example data\n$userId = 1;\n$dataArray = ["data1", "data2", "data3"]; // Your data array\n\n// Insert user data\nforeach ($dataArray as $data) {\n    $stmt = $conn->prepare("INSERT INTO user_data (user_id, data) VALUES (?, ?)");\n    $stmt->bind_param("is", $userId, $data);\n    $stmt->execute();\n}\n\n$stmt->close();\n$conn->close();\n?>