<?php\n// Example user region value (this should come from your user data)\n$currentRegion = 'some_region_value';\n\n// Database connection (replace with your actual connection details)\n$mysqli = new mysqli('localhost', 'username', 'password', 'database');\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Fetch regions\n$query = "SELECT id, region FROM region ORDER BY region";\n$result = $mysqli->query($query);\n\n// Build the dropdown\n$dropdown = "<select name='region'>";\n\nwhile ($row = $result->fetch_assoc()) {\n    $selected = $row['region'] == $currentRegion ? "selected='selected'" : "";\n    $dropdown .= "\r\n<option value='{$row['id']}' {$selected}>{$row['region']}</option>";\n}\n\n$dropdown .= "\r\n</select>";\n\n// Close the connection\n$mysqli->close();\n\n// Output the dropdown\necho $dropdown;\n?>