<?php\nfunction getSearchResults($query) {\n    // Connect to your database\n    $pdo = new PDO('mysql:host=localhost;dbname=yourdb', 'username', 'password');\n\n    // Prepare and execute the search query\n    $stmt = $pdo->prepare("SELECT id, content FROM your_table WHERE MATCH(content) AGAINST(:query IN NATURAL LANGUAGE MODE)");\n    $stmt->execute(['query' => $query]);\n\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nfunction createSnippet($text, $query, $radius = 50) {\n    // Escape special characters in the query for regex\n    $escapedQuery = preg_quote($query, '/');\n    \n    // Find the position of the first match\n    if (preg_match("/\b$escapedQuery\b/i", $text, $matches, PREG_OFFSET_CAPTURE)) {\n        $startPos = max($matches[0][1] - $radius, 0);\n        $endPos = min($matches[0][1] + strlen($matches[0][0]) + $radius, strlen($text));\n        \n        // Extract snippet\n        $snippet = substr($text, $startPos, $endPos - $startPos);\n        \n        // Add ellipses if snippet is taken from the middle\n        if ($startPos > 0) {\n            $snippet = '...' . $snippet;\n        }\n        if ($endPos < strlen($text)) {\n            $snippet .= '...';\n        }\n        \n        // Highlight the query terms\n        $highlightedSnippet = preg_replace("/\b($escapedQuery)\b/i", '<strong>$1</strong>', $snippet);\n\n        return $highlightedSnippet;\n    }\n    return ''; // Return empty if no match found\n}\n\n// Example usage\n$searchQuery = 'your search term';\n$results = getSearchResults($searchQuery);\n\nforeach ($results as $result) {\n    $snippet = createSnippet($result['content'], $searchQuery);\n    echo '<div>';\n    echo '<h3>Result ID: ' . $result['id'] . '</h3>';\n    echo '<p>' . $snippet . '</p>';\n    echo '</div>';\n}\n?>