<?php\n// Function to set the "remember me" cookie\nfunction setRememberMeCookie($userId) {\n    $token = bin2hex(random_bytes(32));\n    $hashedToken = password_hash($token, PASSWORD_DEFAULT);\n\n    // Store $hashedToken in the database associated with the user ID\n    // Example: saveTokenToDatabase($userId, $hashedToken);\n\n    // Set the cookie with secure flags\n    setcookie(\n        'remember_me',\n        $token,\n        [\n            'expires' => time() + (86400 * 30), // 30 days\n            'path' => '/',\n            'domain' => '', // set your domain\n            'secure' => true, // ensure it is set only on HTTPS\n            'httponly' => true, // accessible only through the HTTP protocol\n            'samesite' => 'Strict' // prevent CSRF attacks\n        ]\n    );\n}\n\n// Function to check the "remember me" cookie\nfunction checkRememberMeCookie() {\n    if (isset($_COOKIE['remember_me'])) {\n        $token = $_COOKIE['remember_me'];\n\n        // Retrieve the hashed token from the database\n        // Example: $hashedToken = getTokenFromDatabase($userId);\n\n        if (password_verify($token, $hashedToken)) {\n            // Token is valid, log the user in\n            // Example: logUserIn($userId);\n        } else {\n            // Invalid token, handle the error\n        }\n    }\n}\n\n// Usage example\n// Call setRememberMeCookie() when the user logs in and opts for "remember me"\n// Call checkRememberMeCookie() on subsequent visits to verify the token\n\n?>