$memcache = new Memcache;\n$memcacheHost = '127.0.0.1';\n$memcachePort = 11211;\n$connectionTimeout = 1; // 1 second timeout\n\nif (!$memcache->connect($memcacheHost, $memcachePort, $connectionTimeout)) {\n    error_log("Failed to connect to Memcache server at {$memcacheHost}:{$memcachePort}");\n    // Handle the error appropriately\n} else {\n    // Use the memcache connection\n    // Example: store an object\n    $key = 'example_key';\n    $value = 'example_value';\n    $expiration = 3600; // 1 hour\n    $memcache->set($key, $value, 0, $expiration);\n\n    // Retrieve the object\n    $cachedValue = $memcache->get($key);\n}