<?php\n// Function to filter HTML and inappropriate words from a string\nfunction FilterALLHTML($var) {\n    if ($var != null) {\n        $text = strip_tags($var);\n        $search = array(\n            "/f.?u.?c.?k/i",\n            "/(s|$).?h.?i.?t/i",\n            '/(potspace|mycrib|palbolt|pot space)/i'\n        );\n        $text = preg_replace($search, '', $text);\n        return $text;\n    }\n    return null;\n}\n\n// Array of variables to be filtered\n$fields = array('city', 'state', 'title', 'division');\n\n// Loop through each field and apply the FilterALLHTML function\nforeach ($fields as $field) {\n    if (isset($$field)) {\n        $$field = FilterALLHTML($$field);\n    }\n}\n?>