abstract class ParentClass {\n    protected static $message = "UNTOUCHED";\n\n    public static function yeah() {\n        static::$message = "YEAH";\n    }\n    \n    public static function nope() {\n        static::$message = "NOPE";\n    }\n\n    public static function lateStaticDebug() {\n        return static::$message;\n    }\n}\n\nclass Child extends ParentClass {\n    protected static $message = "UNTOUCHED_CHILD";\n}\n\nParentClass::yeah();\necho ParentClass::lateStaticDebug();  // Outputs: "YEAH"\n\nChild::nope();\necho Child::lateStaticDebug();        // Outputs: "NOPE"\n\nParentClass::yeah();\necho Child::lateStaticDebug();        // Outputs: "NOPE"