class WebSiteUser {\n    private static $common_homepage_content;\n    private $username;\n    private $password_hash;\n\n    public static function loadCommonHomepageContent($dbConnection) {\n        $query = "SELECT content FROM common_content LIMIT 1";\n        $result = $dbConnection->query($query);\n        if ($row = $result->fetch_assoc()) {\n            self::$common_homepage_content = $row['content'];\n        }\n    }\n\n    // Other methods and properties\n}